package Basic;

public class Operators2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int number=2345,a=8;
        int addition=number+a;
        int quotient=addition/3;
        int remainder=quotient%5;
        int finalresult=remainder*5;
		System.out.print("after addition:"+addition);
        System.out.print("after divison:"+quotient);
        System.out.print("after modulus divison:"+remainder);
        System.out.print("after finalresult:"+finalresult);
        
	}

}
