package Basic;

public class PercentageMarks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int subject1=78;
    int subject2=45;
    int subject3=62;
    float obtained = subject1+subject2+subject3;
    float total=300;
    float percentage=(obtained/total)*100;
    System.out.println(percentage);
    
	}

}
